mapper.update:
update t_coffee
set name = #{name,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, create_time, update_time
from t_coffee
where id = #{id,jdbcType=BIGINT}
